import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
// require('dotenv').config();

function App() {
	const ApiKey = process.env.REACT_APP_API_KEY;
	const API_KEY = 'aa612f293de8cfae23f3d56312b5f998';
	const [allData, setAllData] = useState(null);
	const mainUrl = `https://api.themoviedb.org/3/movie/550?api_key=${API_KEY}`;
	const popularUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
	const pagesUrl = `https://api.themoviedb.org/3/search/movie?query=Avengers&api_key=${API_KEY}&page=1`;
	const keywordsUrl = ` https://api.themoviedb.org/3/movie/{movie_id}/keywords?api_key=${API_KEY}`;
	const images = 'https://image.tmdb.org/t/p/w500';
	let queryUrl = {
		api_key: `https://api.themoviedb.org/3/search/movie?`,
		query: '',
	};
	const [movieInfo, setMovieInfo] = useState([]);
	const [inputVal, setInputVal] = useState('');
	const [api, setApi] = useState(null);

	useEffect(() => {
		async function fetch(api) {
			const {
				data: { results },
			} = await axios.get(api);
			setMovieInfo(() => results.filter((img) => img.poster_path !== null));
		}
		fetch();
	}, []);

	function handleInput(e) {
		const query = e.target.value;
		setInputVal(query);
	}
	if (inputVal.length > 0) {
		setApi(pagesUrl);
		fetch(api);
	} else {
		setApi(popularUrl);
	}

	return (
		<div className='App'>
			<div>
				<h1>Movie</h1>
				<input onChange={handleInput} placeholder='Search...' />
			</div>
			<div className='content-wrapper'>
				<div className='movieContent'>
					{movieInfo.map((movie) => {
						return (
							<img
								key={movie.id}
								className='images'
								src={`${images}${movie.poster_path}`}
							></img>
						);
					})}
				</div>
			</div>
		</div>
	);
}

export default App;
