import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import ReUsableApiRequest from './ReUsableApiRequest';
import {
	BrowserRouter as Router,
	Switch,
	Route,
	Redirect,
	useHistory,
} from 'react-router-dom';
import Detail from './Detail';
import Popular from './Popular';
import Search from './Search';
import Home from './Home';

function App() {
	const ApiKey = process.env.REACT_APP_API_KEY;
	const API_KEY = 'aa612f293de8cfae23f3d56312b5f998';
	const [allData, setAllData] = useState(null);
	const mainUrl = `https://api.themoviedb.org/3/movie/550?api_key=${API_KEY}`;
	const popularUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
	const pagesUrl = `https://api.themoviedb.org/3/search/movie?query=Avengers&api_key=${API_KEY}&page=1`;
	let queryUrl = {
		url: 'https://api.themoviedb.org/3/search/movie?',
		api_key: API_KEY,
		query: '',
	};
	const [movieInfo, setMovieInfo] = useState([]);
	const [inputVal, setInputVal] = useState('');
	const [submitted, setSubmitted] = useState(false);
	const history = useHistory();

	function handleInput(e) {
		const query = e.target.value;
		setInputVal(query);
	}

	function handleKeyDown(e) {
		if (e.key === 'Enter') {
			history.push('/search');
			setSubmitted(true);
			console.log(submitted);
			console.log(inputVal);
		}
	}

	return (
		<div className='App'>
			<div>
				<h1>Movie</h1>
				<input
					onKeyDown={handleKeyDown}
					onChange={handleInput}
					placeholder='Search...'
				/>
			</div>

			<Router>
				<Switch>
					<Route path='/' exact restrict component={Home} />
					<Route path='/search' component={Search} />
					<Route path='/details/:id' component={Detail} />
				</Switch>
			</Router>
		</div>
	);
}

export default App;
