import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

export default function Detail({ match }) {
	const [movieDetals, setMovieDetails] = useState([]);
	const API_KEY = 'aa612f293de8cfae23f3d56312b5f998';
	useEffect(() => {
		fetchMovie();
		console.log(match);
	}, []);

	const fetchMovie = async () => {
		const { data } = await axios.get(
			`https://api.themoviedb.org/3/movie/${match.params.id}?api_key=${API_KEY}`
		);
		setMovieDetails(data);
		// console.log(data);
	};

	return (
		<div>
			{movieDetals.map((movie) => {
				return (
					<div>
						<h1 className='detail'>{movie.title}</h1>
						<h1 className='detail'>{movie.title}</h1>
					</div>
				);
			})}
		</div>
	);
}
