import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
// require('dotenv').config();

function App() {
	const ApiKey = process.env.REACT_APP_API_KEY;
	const API_KEY = 'aa612f293de8cfae23f3d56312b5f998';
	const [allData, setAllData] = useState(null);
	const mainUrl = `https://api.themoviedb.org/3/movie/550?api_key=${API_KEY}`;
	const popularUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
	const pagesUrl = `https://api.themoviedb.org/3/search/movie?query=Avengers&api_key=${API_KEY}&page=2`;
	const images = 'https://image.tmdb.org/t/p/w500';
	const [movieInfo, setMovieInfo] = useState([]);

	useEffect(() => {
		async function fetch() {
			const {
				data: { results },
			} = await axios.get(pagesUrl);
			setMovieInfo(results);
		}
		fetch();
	}, []);

	// console.log(movieInfo);

	return (
		<div className='App'>
			{movieInfo.map((movie) => {
				return (
					<div
						className='movieImage'
						style={{ display: 'flex', flexDirection: 'column' }}
					>
						<img
							className='images'
							src={`${images}${movie.poster_path}`}
							style={{
								display: movie.poster_path ? 'block' : 'none',
								width: '240px',
							}}
						></img>
					</div>
				);
			})}
		</div>
	);
}

export default App;
